// TrendCard.js
import React from 'react';
import styled from 'styled-components';

const Card = styled.div`
  width: 300px;
  height: 400px;
  background-color: white;
  border-radius: 10px;
  box-shadow: 0 4px 8px rgba(0, 0, 0, 0.2);
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  position: absolute;
  transition: transform 0.5s, opacity 0.5s;
  transform: ${(props) => props.swipeDirection && `translateX(${props.swipeDirection === 'Right' ? '100%' : '-100%'})`};
  opacity: ${(props) => (props.swipeDirection ? 0 : 1)};
`;

const Image = styled.img`
  width: 100%;
  height: 80%;
  border-radius: 10px 10px 0 0;
  object-fit: cover;
`;

const Info = styled.div`
padding:10px;
  text-align: center;
`;

const TrendCard = ({ trend, swipeDirection }) => {
  return (
    <Card swipeDirection={swipeDirection}>
      <Image src={trend.image} alt={trend.name} />
      <Info>
        <h2>{trend.name}</h2>
        <p>{trend.description}</p>
      </Info>
    </Card>
  );
};

export default TrendCard;
