import React from 'react';
import TrendCard from './TrendCard'; // Adjust the path as needed
import styled from 'styled-components';

const Container = styled.div`
  display: flex;
  flex-wrap: wrap;
  justify-content: center;
  align-items: center; /* Center the content vertically */
  padding: 20px;
  gap: 20px;
  height: 100vh; /* Ensure the container takes the full viewport height */
  background: url(${process.env.PUBLIC_URL + '/images/bgimage2.jpeg'}) no-repeat center center fixed;
  background-size: cover; /* Ensure the background image covers the entire container */
`;

const Heading = styled.h1`
  margin-bottom: 20px;
  font-size: 24px;
  font-weight: bold;
  text-align: center;
  width: 100%;
  font-family: "Rammetto One", sans-serif; /* Add the font family here */
`;

const LikedTrend = styled.div`
  margin: 10px 0;
  padding: 10px;
  background-color: white;
  border-radius: 10px;
  box-shadow: 0 4px 8px rgba(0, 0, 0, 0.2);
  display: flex;
  flex-direction: column;
  align-items: center;
`;

const TrendImage = styled.img`
  width: 100%;
  height: auto;
  max-width: 300px;
  border-radius: 10px;
`;

const LikedTrendsPage = ({ likedTrends }) => {
  return (
    <div>
      <Heading>Liked Trends</Heading>
      <Container>
        {likedTrends.map((trend, index) => (
          <TrendCard key={index} trend={trend} swipeDirection={null} />
        ))}
      </Container>
    </div>
  );
};

export default LikedTrendsPage;
