import React from 'react';
import TrendCard from './TrendCard'; // Adjust the path as needed
import styled from 'styled-components';

const Container = styled.div`
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(300px, 1fr)); /* Adjust column width as needed */
  gap: 20px; /* Adjust gap between cards */
  padding: 20px;
`;

const Heading = styled.h1`
  margin-bottom: 20px;
  font-size: 24px;
  font-weight: bold;
  text-align: center;
  width: 100%;
`;

const LikedTrendsPage = ({ likedTrends }) => {
  return (
    <div>
      <Heading>Liked Trends</Heading>
      <Container>
        {likedTrends.map((trend, index) => (
          <StyledTrendCard key={index} trend={trend} swipeDirection={null} />
        ))}
      </Container>
    </div>
  );
};

const StyledTrendCard = styled(TrendCard)`
  width: 100%;
`;

export default LikedTrendsPage;
