//SwipeableContainer
import React, { useState, useEffect } from 'react';
import { useSwipeable } from 'react-swipeable';
import TrendCard from './TrendCard';
import styled from 'styled-components';
import { useHistory } from 'react-router-dom';

const Container = styled.div`
  display: flex;
  justify-content: center;
  align-items: center;
  height: 100vh;
  position: relative;
  background-color: lightgreen;
`;

const ButtonContainer = styled.div`
  position: absolute;
  bottom: 100px;
  display: flex;
  gap: 20px;
`;

const SwipeButton = styled.button`
  background-color: white;
  border: 1px solid #ccc;
  border-radius: 5px;
  padding: 10px 20px;
  font-size: 16px;
  cursor: pointer;
  &:hover {
    background-color: #f0f0f0;
  }
`;

const EndCard = styled.div`
  position: absolute;
  bottom: 50px;
  width: 100%;
  text-align: center;
  font-size: 18px;
  cursor: pointer;
`;

const SwipeableContainer = ({ trends, onLike, onDislike, onShowRelated }) => {
  const [currentIndex, setCurrentIndex] = useState(0);
  const [swipeDirection, setSwipeDirection] = useState(null);
  const [likedTrends, setLikedTrends] = useState([]);

  const history = useHistory();

  useEffect(() => {
    if (swipeDirection === 'Right') {
      setLikedTrends((prevLikedTrends) => [...prevLikedTrends, trends[currentIndex]]);
    }
  }, [swipeDirection, currentIndex, trends]);

  useEffect(() => {
    if (swipeDirection) {
      const timeout = setTimeout(() => {
        setCurrentIndex((prevIndex) => prevIndex + 1);
        setSwipeDirection(null);
      }, 500); // match the duration with CSS transition
      return () => clearTimeout(timeout);
    }
  }, [swipeDirection]);

  const handleSwiped = (direction) => {
    setSwipeDirection(direction);
    if (direction === 'Right') {
      onLike(trends[currentIndex]);
    } else if (direction === 'Left') {
      onDislike(trends[currentIndex]);
    } else if (direction === 'Up') {
      onShowRelated(trends[currentIndex]);
    }
  };

  const handlers = useSwipeable({
    onSwiped: (eventData) => handleSwiped(eventData.dir),
    preventDefaultTouchmoveEvent: true,
    trackMouse: true,
  });

  const handleSwipeLeft = () => handleSwiped('Left');
  const handleSwipeRight = () => handleSwiped('Right');

  const handleEndCardClick = () => {
    // Navigate to the new page showing liked trends
    history.push('/liked-trends', { likedTrends });
  };

  return (
    <Container {...handlers}>
      {trends[currentIndex] && <TrendCard trend={trends[currentIndex]} swipeDirection={swipeDirection} />}
      <ButtonContainer>
        <SwipeButton onClick={handleSwipeLeft}>Swipe Left</SwipeButton>
        <SwipeButton onClick={handleSwipeRight}>Swipe Right</SwipeButton>
      </ButtonContainer>
      <EndCard onClick={handleEndCardClick}>Click here to see liked trends</EndCard>
    </Container>
  );
};

export default SwipeableContainer;
