import React from 'react';
import styled, { keyframes } from 'styled-components';

const swipeAnimation = (direction) => keyframes`
  0% {
    transform: translateX(0);
  }
  100% {
    transform: translateX(${direction === 'left' ? '-100%' : '100%'});
  }
`;

const Card = styled.div`
  width: 90%;
  max-width: 300px;
  height: 400px;
  background-color: white;
  border-radius: 10px;
  box-shadow: 0 4px 8px rgba(0, 0, 0, 0.2);
  display: flex;
  flex-direction: column;
  justify-content: flex-start;
  align-items: center;
  margin-top: 30px;
  font-family: 'Concert One', sans-serif;
  animation: ${({ swipeDirection }) => swipeDirection && swipeAnimation(swipeDirection)} 0.5s ease-out;
`;

const Image = styled.img`
  width: 100%;
  height: 85%;  /* Increase the height of the image */
  border-radius: 10px 10px 0 0;
  object-fit: cover;
`;

const Info = styled.div`
  width: 100%;
  padding: 5px;  /* Decrease the padding */
  text-align: center;
  font-family: 'Concert One', sans-serif;
`;

const TrendCard = ({ trend, swipeDirection }) => {
  return (
    <Card swipeDirection={swipeDirection}>
      <Image src={trend.image} alt={trend.name} />
      <Info>
        <h2>{trend.name}</h2>
      </Info>
    </Card>
  );
};

export default TrendCard;
