import React, { useState, useEffect } from 'react';
import { useSwipeable } from 'react-swipeable';
import TrendCard from './TrendCard';
import styled from 'styled-components';
import { useNavigate } from 'react-router-dom'; // Import useNavigate instead of useHistory

const Container = styled.div`
  display: flex;
  flex-direction: column;
  justify-content: flex-start;
  align-items: center;
  height: 100vh;
  padding-top: 20px;
  background-color: lightgreen;
`;

const Heading = styled.h1`
  margin-top: 50px;
  font-size: 35px;
  font-weight: bold;
`;

const SwipeButton = styled.button`
  background-color: white;
  border: 1px solid #ccc;
  border-radius: 5px;
  padding: 10px 20px;
  font-size: 16px;
  cursor: pointer;
  &:hover {
    background-color: #f0f0f0;
  }
`;

const EndCard = styled.div`
  position: absolute;
  bottom: 50px;
  width: 100%;
  text-align: center;
  font-size: 18px;
  cursor: pointer;
`;

const SwipeableContainer = ({ trends }) => {
  const [currentIndex, setCurrentIndex] = useState(0);
  const [swipeDirection, setSwipeDirection] = useState(null);

  const navigate = useNavigate();

  useEffect(() => {
    const handleSwipe = (direction) => {
      setSwipeDirection(direction);
      setTimeout(() => {
        setCurrentIndex((prevIndex) => (prevIndex + 1) % trends.length);
        setSwipeDirection(null);
      }, 500);
    };

    const handlers = useSwipeable({
      onSwiped: (eventData) => handleSwipe(eventData.dir),
      preventDefaultTouchmoveEvent: true,
      trackMouse: true,
    });

    const handleEndCardClick = () => {
      navigate('/liked-trends');
    };

    return (
      <Container {...handlers}>
        <Heading>Swipe & Style</Heading>
        {trends[currentIndex] && <TrendCard trend={trends[currentIndex]} swipeDirection={swipeDirection} />}
        <SwipeButton onClick={() => handleSwipe('Left')}>Swipe Left</SwipeButton>
        <SwipeButton onClick={() => handleSwipe('Right')}>Swipe Right</SwipeButton>
        <EndCard onClick={handleEndCardClick}>Click here to see liked trends</EndCard>
      </Container>
    );
  });
};

export default SwipeableContainer;
