import React, { useState } from 'react';
import TrendCard from './TrendCard'; // Adjust the path as needed
import styled from 'styled-components';

const Container = styled.div`
  display: flex;
  overflow-x: auto; /* Allow horizontal scrolling */
  padding: 20px;
`;

const Heading = styled.h1`
  margin-bottom: 20px;
  font-size: 24px;
  font-weight: bold;
  text-align: center;
  width: 100%;
`;

const ButtonContainer = styled.div`
  text-align: center;
  margin-top: 20px;
`;

const LikedTrendsPage = ({ likedTrends }) => {
  const [swiped, setSwiped] = useState(false);

  const handleSwipeAll = () => {
    setSwiped(true);
  };

  return (
    <div>
      <Heading>Liked Trends</Heading>
      <Container>
        {likedTrends.map((trend, index) => (
          <TrendCard key={index} trend={trend} swipeDirection={swiped ? 'right' : null} />
        ))}
      </Container>
      <ButtonContainer>
        <button onClick={handleSwipeAll}>Swipe All</button>
      </ButtonContainer>
    </div>
  );
};

export default LikedTrendsPage;
