import React from 'react';
import styled, { keyframes } from 'styled-components';

const slideAnimation = keyframes`
  0% {
    transform: translateX(0);
    opacity: 1;
  }
  100% {
    transform: translateX(100%);
    opacity: 0;
  }
`;

const Card = styled.div`
  width: 300px; /* Adjust width as needed */
  height: 400px;
  background-color: white;
  border-radius: 10px;
  box-shadow: 0 4px 8px rgba(0, 0, 0, 0.2);
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  margin-right: 20px; /* Adjust margin between cards */
  animation: ${({ swipeDirection }) => swipeDirection === 'right' ? slideAnimation : 'none'} 0.5s ease-out forwards; /* Animation definition */
`;

const Image = styled.img`
  width: 100%;
  height: 80%;
  border-radius: 10px 10px 0 0;
  object-fit: cover;
`;

const Info = styled.div`
  margin: 10px;
  text-align: center;
`;

const TrendCard = ({ trend, swipeDirection }) => {
  return (
    <Card swipeDirection={swipeDirection}>
      <Image src={trend.image} alt={trend.name} />
      <Info>
        <h2>{trend.name}</h2>
      </Info>
    </Card>
  );
};

export default TrendCard;
