// SwipeableContainer.js
import React, { useState } from 'react';
import { useSwipeable } from 'react-swipeable';
import TrendCard from './TrendCard'; // Adjust the path as needed
import styled from 'styled-components';

const Container = styled.div`
  display: flex;
  justify-content: center;
  align-items: center;
  height: 100vh;
  position: relative;
  background-color: black; // Add a black background color
`;

const SwipeableContainer = ({ trends, onLike, onDislike, onShowRelated }) => {
  const [currentIndex, setCurrentIndex] = useState(0);

  const handleSwiped = (eventData) => {
    if (eventData.dir === 'Right') {
      onLike(trends[currentIndex]);
      setCurrentIndex(currentIndex + 1);
    } else if (eventData.dir === 'Left') {
      onDislike(trends[currentIndex]);
      setCurrentIndex(currentIndex + 1);
    } else if (eventData.dir === 'Up') {
      onShowRelated(trends[currentIndex]);
    }
  };

  const handlers = useSwipeable({
    onSwiped: (eventData) => handleSwiped(eventData),
    preventDefaultTouchmoveEvent: true,
    trackMouse: true,
  });

  return (
    <Container {...handlers}>
      {trends[currentIndex] && <TrendCard trend={trends[currentIndex]} />}
    </Container>
  );
};

export default SwipeableContainer;
