// src/components/TrendCard.js
import React from 'react';
import styled from 'styled-components';

const Card = styled.div`
  width: 300px;
  height: 400px;
  border-radius: 10px;
  overflow: hidden;
  box-shadow: 0 4px 8px rgba(0, 0, 0, 0.2);
  background: black; /* Background color changed to black */
  color: white; /* Text color changed to white */
  display: flex;
  flex-direction: column;
  align-items: center;
`;

const TrendImage = styled.img`
  width: 100%;
  height: 70%;
  object-fit: cover;
  border-top-left-radius: 10px;
  border-top-right-radius: 10px;
`;

const TrendInfo = styled.div`
  padding: 15px;
  text-align: center;
`;

const Title = styled.h3`
  font-size: 1.5rem;
  margin-bottom: 10px;
`;

const Description = styled.p`
  font-size: 1rem;
`;

const TrendCard = ({ trend }) => (
  <Card>
    <TrendImage src={trend.image} alt={trend.name} />
    <TrendInfo>
      <Title>{trend.name}</Title>
      <Description>{trend.description}</Description>
    </TrendInfo>
  </Card>
);

export default TrendCard;
