// TrendCard.js
import React from 'react';
import styled from 'styled-components';

const Card = styled.div`
  width: 300px;
  height: 400px;
  border-radius: 10px;
  overflow: hidden;
  box-shadow: 0 4px 8px rgba(0,0,0,0.2);
  background: white;
  display: flex;
  flex-direction: column;
  align-items: center;
`;

const TrendImage = styled.img`
  width: 100%;
  height: 70%;
  object-fit: cover;
`;

const TrendInfo = styled.div`
  padding: 10px;
  text-align: center;
`;

const TrendCard = ({ trend }) => (
  <Card>
    <TrendImage src={trend.image} alt={trend.name} />
    <TrendInfo>
      <h3>{trend.name}</h3>
      <p>{trend.description}</p>
    </TrendInfo>
  </Card>
);

export default TrendCard;
