// SwipeableContainer.js
import React, { useState } from 'react';
import { useSwipeable } from 'react-swipeable';
import TrendCard from './TrendCard'; // Adjust the path as needed
import styled from 'styled-components';

const Container = styled.div`
  display: flex;
  justify-content: center;
  align-items: center;
  height: 100vh;
  position: relative;
  background-color: black;
`;

const ButtonContainer = styled.div`
  position: absolute;
  bottom: 20px;
  display: flex;
  gap: 20px;
`;

const SwipeButton = styled.button`
  background-color: white;
  border: 1px solid #ccc;
  border-radius: 5px;
  padding: 10px 20px;
  font-size: 16px;
  cursor: pointer;
  &:hover {
    background-color: #f0f0f0;
  }
`;

const SwipeableContainer = ({ trends, onLike, onDislike, onShowRelated }) => {
  const [currentIndex, setCurrentIndex] = useState(0);

  const handleSwiped = (direction) => {
    if (direction === 'Right') {
      onLike(trends[currentIndex]);
      setCurrentIndex(currentIndex + 1);
    } else if (direction === 'Left') {
      onDislike(trends[currentIndex]);
      setCurrentIndex(currentIndex + 1);
    } else if (direction === 'Up') {
      onShowRelated(trends[currentIndex]);
    }
  };

  const handlers = useSwipeable({
    onSwiped: (eventData) => handleSwiped(eventData.dir),
    preventDefaultTouchmoveEvent: true,
    trackMouse: true,
  });

  const handleSwipeLeft = () => handleSwiped('Left');
  const handleSwipeRight = () => handleSwiped('Right');

  return (
    <Container {...handlers}>
      {trends[currentIndex] && <TrendCard trend={trends[currentIndex]} />}
      <ButtonContainer>
        <SwipeButton onClick={handleSwipeLeft}>Swipe Left</SwipeButton>
        <SwipeButton onClick={handleSwipeRight}>Swipe Right</SwipeButton>
      </ButtonContainer>
    </Container>
  );
};

export default SwipeableContainer;
