import React, { useState } from 'react';
import styled from 'styled-components';
import { useNavigate } from 'react-router-dom';
import TrendCard from './TrendCard';

const Container = styled.div`
  display: flex;
  flex-direction: column;
  justify-content: flex-start;
  align-items: center;
  height: 100vh;
  padding-top: 20px;
  background: url(${process.env.PUBLIC_URL + '/images/bg image.jpeg'}) no-repeat center center fixed;
  background-size: cover;

  @media (max-width: 768px) {
    padding-top: 10px;
  }
`;

const Heading = styled.h1`
  margin-top: 40px;
  font-size: 35px;
  font-weight: bold;
  font-family: "Rammetto One", sans-serif;

  @media (max-width: 768px) {
    font-size: 28px;
  }
`;

const ButtonContainer = styled.div`
  position: absolute;
  bottom: 100px;
  display: flex;
  gap: 10px;

  @media (max-width: 768px) {
    bottom: 70px;
    gap: 5px;
  }
`;

const SwipeButton = styled.button`
  background-color: white;
  border: 1px solid #ccc;
  border-radius: 20px;
  padding: 10px 30px;
  font-size: 16px;
  cursor: pointer;
  transition: background-color 0.3s;

  &:hover {
    background-color: #f0f0f0;
  }

  @media (max-width: 768px) {
    padding: 8px 20px;
    font-size: 14px;
  }
`;

const EndCardContainer = styled.div`
  display: flex;
  justify-content: center;
  align-items: center;
  height: 400px;
  width: 300px;
  background-color: white;
  border-radius: 10px;
  box-shadow: 0 4px 8px rgba(0, 0, 0, 0.2);
  text-align: center;
  font-family: 'Concert One', sans-serif;
  cursor: pointer;
`;

const EndCardText = styled.p`
  font-size: 18px;
  padding: 20px;
`;

const EndCardButton = styled.button`
  position: absolute;
  bottom: 50px;
  width: 80%;
  max-width: 300px;
  text-align: center;
  font-size: 18px;
  cursor: pointer;
  background-color: white;
  border: 1px solid #ccc;
  border-radius: 20px;
  padding: 10px 30px;
  transition: background-color 0.3s;

  &:hover {
    background-color: #f0f0f0;
  }

  @media (max-width: 768px) {
    bottom: 30px;
    padding: 8px 20px;
    font-size: 16px;
  }
`;

const SwipeableContainer = ({ trends, onLike, onDislike }) => {
  const [currentIndex, setCurrentIndex] = useState(0);
  const [swipeDirection, setSwipeDirection] = useState(null);
  const navigate = useNavigate();

  const handleSwipe = (direction) => {
    setSwipeDirection(direction);
    setTimeout(() => {
      setCurrentIndex((prevIndex) => prevIndex + 1);
      setSwipeDirection(null);
    }, 500);
  };

  const handleSwipeLeft = () => {
    handleSwipe('left');
    onDislike(trends[currentIndex]);
  };

  const handleSwipeRight = () => {
    handleSwipe('right');
    onLike(trends[currentIndex]);
  };

  const handleEndCardClick = () => {
    navigate('/liked-trends');
  };

  return (
    <Container>
      <Heading>Swipe & Style</Heading>
      {currentIndex < trends.length ? (
        <>
          <TrendCard trend={trends[currentIndex]} swipeDirection={swipeDirection} />
          <ButtonContainer>
            <SwipeButton onClick={handleSwipeLeft}>Swipe Left</SwipeButton>
            <SwipeButton onClick={handleSwipeRight}>Swipe Right</SwipeButton>
          </ButtonContainer>
        </>
      ) : (
        <EndCardContainer onClick={handleEndCardClick}>
          <EndCardText>These are the trends you liked. Shop more like this.</EndCardText>
        </EndCardContainer>
      )}
      <EndCardButton onClick={handleEndCardClick}>See Liked Trends</EndCardButton>
    </Container>
  );
};

export default SwipeableContainer;
