import React, { useState } from 'react';
import TrendCard from './TrendCard'; // Adjust the path as needed
import styled from 'styled-components';

const Container = styled.div`
  display: flex;
  overflow-x: auto; /* Allow horizontal scrolling */
  padding: 20px;
`;

const Heading = styled.h1`
  margin-bottom: 20px;
  font-size: 24px;
  font-weight: bold;
  text-align: center;
  width: 100%;
`;

const ButtonContainer = styled.div`
  text-align: center;
  margin-top: 20px;
`;

const LikedTrendsPage = ({ likedTrends }) => {
  const [currentIndex, setCurrentIndex] = useState(0);

  const handleSwipeLeft = () => {
    setCurrentIndex((prevIndex) => prevIndex - 1);
  };

  const handleSwipeRight = () => {
    setCurrentIndex((prevIndex) => prevIndex + 1);
  };

  return (
    <div>
      <Heading>Liked Trends</Heading>
      <Container>
        {likedTrends.map((trend, index) => (
          <TrendCard
            key={index}
            trend={trend}
            swipeDirection={index === currentIndex ? 'right' : null} // Trigger animation based on currentIndex
          />
        ))}
      </Container>
      <ButtonContainer>
        <button onClick={handleSwipeLeft} disabled={currentIndex === 0}>Swipe Left</button>
        <button onClick={handleSwipeRight} disabled={currentIndex === likedTrends.length - 1}>Swipe Right</button>
      </ButtonContainer>
    </div>
  );
};

export default LikedTrendsPage;
