import React, { useState } from 'react';
import styled, { keyframes } from 'styled-components';
import { useNavigate } from 'react-router-dom';
import TrendCard from './TrendCard';

const noise1 = keyframes`
  0% { clip-path: inset(20px 0 80px 0); }
  100% { clip-path: inset(50px 0 50px 0); }
`;

const noise2 = keyframes`
  0% { clip-path: inset(20px 0 80px 0); }
  100% { clip-path: inset(50px 0 50px 0); }
`;

const glitch = keyframes`
  1% { transform: rotateX(10deg) skewX(90deg); }
  2% { transform: rotateX(0deg) skewX(0deg); }
`;

const fudge = keyframes`
  from { transform: translate(0px, 0px); }
  to { transform: translate(0px, 2%); }
`;

const glitch2 = keyframes`
  1% { transform: rotateX(10deg) skewX(70deg); }
  2% { transform: rotateX(0deg) skewX(0deg); }
`;

const HeadingContainer = styled.div`
  position: relative;
  margin-top: 40px;
  text-align: center;
`;

const Glitch = styled.div`
  font-family: 'Oswald', sans-serif;
  color: rgb(223, 191, 191);
  font-size: 5vw;
  position: relative;
  animation: ${glitch} 5s 5s infinite;

  &::before,
  &::after {
    content: attr(data-text);
    position: absolute;
    background: black;
    overflow: hidden;
  }

  &::before {
    left: -2px;
    text-shadow: -5px 0 magenta;
    animation: ${noise1} 3s linear infinite alternate-reverse, ${glitch} 5s 5.05s infinite;
  }

  &::after {
    left: 2px;
    text-shadow: -5px 0 lightgreen;
    animation: ${noise2} 3s linear infinite alternate-reverse, ${glitch} 5s 5s infinite;
  }
`;

const Glow = styled.div`
  font-family: 'Oswald', sans-serif;
  color: transparent;
  text-shadow: 0 0 1000px rgb(223, 191, 191);
  position: absolute;
  top: 0;
`;

const Subtitle = styled.p`
  font-family: Arial, Helvetica, sans-serif;
  font-weight: 100;
  font-size: 0.8vw;
  color: rgba(165, 141, 141, 0.4);
  text-transform: uppercase;
  letter-spacing: 1em;
  text-align: center;
  position: absolute;
  left: 17%;
  animation: ${glitch2} 5s 5.02s infinite;
`;

const Scanlines = styled.div`
  overflow: hidden;
  mix-blend-mode: difference;
  position: absolute;
  width: 100%;
  height: 100%;
  top: 0;
  left: 0;
  background: repeating-linear-gradient(
    to bottom,
    transparent 0%,
    rgba(255, 255, 255, 0.05) 0.5%,
    transparent 1%
  );
  animation: ${fudge} 7s ease-in-out alternate infinite;
`;

const Container = styled.div`
  display: flex;
  flex-direction: column;
  justify-content: flex-start;
  align-items: center;
  height: 100vh;
  padding-top: 20px;
  background: url(${process.env.PUBLIC_URL + '/images/bgimage2.jpeg'}) no-repeat center center fixed;
  background-size: cover;

  @media (max-width: 768px) {
    padding-top: 10px;
  }
`;

const ButtonContainer = styled.div`
  position: absolute;
  bottom: 100px;
  display: flex;
  gap: 10px;

  @media (max-width: 768px) {
    bottom: 70px;
    gap: 5px;
  }
`;

const SwipeButton = styled.button`
  background-color: white;
  border: 1px solid #ccc;
  border-radius: 20px;
  padding: 10px 30px;
  font-size: 16px;
  cursor: pointer;
  transition: background-color 0.3s;

  &:hover {
    background-color: #f0f0f0;
  }

  @media (max-width: 768px) {
    padding: 8px 20px;
    font-size: 14px;
  }
`;

const EndCardContainer = styled.div`
  display: flex;
  justify-content: center;
  align-items: center;
  height: 400px;
  width: 300px;
  background-color: white;
  border-radius: 10px;
  box-shadow: 0 4px 8px rgba(0, 0, 0, 0.2);
  text-align: center;
  font-family: 'Concert One', sans-serif;
  cursor: pointer;
`;

const EndCardText = styled.p`
  font-size: 18px;
  padding: 20px;
`;

const EndCardButton = styled.button`
  position: absolute;
  bottom: 50px;
  width: 80%;
  max-width: 300px;
  text-align: center;
  font-size: 18px;
  cursor: pointer;
  background-color: white;
  border: 1px solid #ccc;
  border-radius: 20px;
  padding: 10px 30px;
  transition: background-color 0.3s;

  &:hover {
    background-color: #f0f0f0;
  }

  @media (max-width: 768px) {
    bottom: 30px;
    padding: 8px 20px;
    font-size: 16px;
  }
`;

const SwipeableContainer = ({ trends, onLike, onDislike }) => {
  const [currentIndex, setCurrentIndex] = useState(0);
  const [swipeDirection, setSwipeDirection] = useState(null);
  const navigate = useNavigate();

  const handleSwipe = (direction) => {
    setSwipeDirection(direction);
    setTimeout(() => {
      setCurrentIndex((prevIndex) => prevIndex + 1);
      setSwipeDirection(null);
    }, 500);
  };

  const handleSwipeLeft = () => {
    handleSwipe('left');
    onDislike(trends[currentIndex]);
  };

  const handleSwipeRight = () => {
    handleSwipe('right');
    onLike(trends[currentIndex]);
  };

  const handleEndCardClick = () => {
    navigate('/liked-trends');
  };

  return (
    <Container>
      <HeadingContainer>
        <Glitch data-text="Swipe & Style">Swipe & Style</Glitch>
        <Glow>Swipe & Style</Glow>
        <Subtitle>IMAGINE YOUR STYLE</Subtitle>
      </HeadingContainer>
      <Scanlines />
      {currentIndex < trends.length ? (
        <>
          <TrendCard trend={trends[currentIndex]} swipeDirection={swipeDirection} />
          <ButtonContainer>
            <SwipeButton onClick={handleSwipeLeft}>Swipe Left</SwipeButton>
            <SwipeButton onClick={handleSwipeRight}>Swipe Right</SwipeButton>
          </ButtonContainer>
        </>
      ) : (
        <EndCardContainer onClick={handleEndCardClick}>
          <EndCardText>These are the trends you liked. Shop more like this.</EndCardText>
        </EndCardContainer>
      )}
      <EndCardButton onClick={handleEndCardClick}>See Liked Trends</EndCardButton>
    </Container>
  );
};

export default SwipeableContainer;
