import React, { useState } from 'react';
import TrendCard from './TrendCard';
import styled from 'styled-components';
import { useNavigate } from 'react-router-dom';

const Container = styled.div`
  display: flex;
  flex-direction: column;
  justify-content: flex-start;
  align-items: center;
  height: 100vh;
  padding-top: 20px;
  background-color: lightgreen;
`;

const Heading = styled.h1`
  margin-top: 40px;
  font-size: 35px;
  font-weight: bold;
`;

const ButtonContainer = styled.div`
  position: absolute;
  bottom: 100px;
  display: flex;
  gap: 10px;left: 50px; 
`;

const SwipeButton = styled.button`
  background-color: white;
  border: 1px solid #ccc;
  border-radius: 20px;
  padding: 10px 30px;
  font-size: 16px;
  
  cursor: pointer;
  &:hover {
    background-color: #f0f0f0;
  }
`;

const EndCard = styled.div`
  position: absolute;
  bottom: 50px;
  width: 100%;
  text-align: center;
  font-size: 18px;
  cursor: pointer;
`;

const SwipeableContainer = ({ trends, onLike, onDislike }) => {
  const [currentIndex, setCurrentIndex] = useState(0);
  const [swipeDirection, setSwipeDirection] = useState(null);
  const navigate = useNavigate();

  const handleSwipe = (direction) => {
    setSwipeDirection(direction);
    setTimeout(() => {
      setCurrentIndex((prevIndex) => (prevIndex + 1) % trends.length);
      setSwipeDirection(null);
    }, 500);
  };

  const handleSwipeLeft = () => {
    handleSwipe('left');
    onDislike(trends[currentIndex]); // Add your onDislike logic
  };

  const handleSwipeRight = () => {
    handleSwipe('right');
    onLike(trends[currentIndex]); // Add your onLike logic
  };

  const handleEndCardClick = () => {
    navigate('/liked-trends');
  };

  return (
    <Container>
      <Heading>Swipe & Style</Heading>
      <TrendCard trend={trends[currentIndex]} swipeDirection={swipeDirection} />
      <ButtonContainer>
        <SwipeButton onClick={handleSwipeLeft}>Swipe Left</SwipeButton>
        <SwipeButton onClick={handleSwipeRight}>Swipe Right</SwipeButton>
      </ButtonContainer>
      <EndCard onClick={handleEndCardClick}>Click here to see liked trends</EndCard>
    </Container>
  );
};

export default SwipeableContainer;
